formulas.expression:
/*start date*/
TODAY() +
/*add 1 days if Saturday*/
case(
  MOD(
    TODAY() - DATE(1900, 1, 1), 7),5,1,0
)
+
/*add number of business days*/
({!fx_Date_InitialCompleteDate} - TODAY())
+
/*add number of non working days = 2 * number of weekends*/
2 * (
        FLOOR(
          (({!fx_Date_InitialCompleteDate} - TODAY()) +
            case(MOD(TODAY() - DATE(1900, 1, 1), 7),
                    0,0, /*Monday*/
                    1,1,
                    2,2,
                    3,3,
                    4,4,
                    5,-1, /*treat Saturday as Sunday*/
                    6,-1,
                    0
                    )
          )/5
        )
)
+ {!var_HolidayCount}
